contracts/HyperIndexVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./SmartIndexVault.sol";
import "./interfaces/IHyperIndexVault.sol";

/**
 * @title HyperIndexVault
 * @dev ERC4626-based vault with LayerZero cross-chain messaging integration
 * @notice Extends SmartIndexVault with cross-chain capabilities for HyperIndex ecosystem
 */
contract HyperIndexVault is SmartIndexVault, IHyperIndexVault {

    constructor(
        IERC20 _asset,
        string memory _name,
        string memory _symbol
    ) SmartIndexVault(_asset, _name, _symbol) {
        // Constructor for template - actual initialization in initialize()
    }

    // LayerZero integration
    address public layerZeroEndpoint;
    uint256 public indexTokenId;

    // Cross-chain configuration
    mapping(uint32 => bytes32) public trustedRemotes; // chainId => trustedRemote
    mapping(address => bool) public authorizedSenders;

    // Rebalancing configuration  
    address[] public underlyingTokens;
    uint256[] public targetAllocations; // in basis points (10000 = 100%)
    uint256 public rebalanceThreshold = 500; // 5% threshold for rebalancing
    uint256 public lastRebalanceTime;

    // Fee configuration
    uint256 public managementFeeBps = 200; // 2%
    uint256 public performanceFeeBps = 2000; // 20%
    uint256 public lastFeeCollection;

    // Additional events (interface events are inherited)

    event TrustedRemoteSet(uint32 chainId, bytes32 trustedRemote);
    event UnderlyingTokenAdded(address token, uint256 allocation);
    event UnderlyingTokenRemoved(uint256 index);
    event TargetAllocationsUpdated(uint256[] newAllocations);

    modifier onlyLayerZero() {
        require(msg.sender == layerZeroEndpoint, "Only LayerZero endpoint");
        _;
    }

    modifier onlyAuthorizedSender() {
        require(authorizedSenders[msg.sender], "Not authorized sender");
        _;
    }

    /**
     * @dev Initialize the vault with LayerZero integration
     */
    function initialize(
        address asset_,
        uint256 indexTokenId_,
        address admin,
        string calldata name_,
        string calldata symbol_,
        address lzEndpoint_,
        address[] calldata tokens_,
        uint256[] calldata targetAllocations_
    ) external override {
        require(address(asset()) == address(0), "Already initialized");
        require(lzEndpoint_ != address(0), "Invalid LayerZero endpoint");
        require(tokens_.length == targetAllocations_.length, "Length mismatch");
        require(_validateAllocations(targetAllocations_), "Invalid allocations");

        // Initialize parent vault through internal methods
        // Note: SmartIndexVault initialize method would need to be available

        // Set vault-specific parameters
        indexTokenId = indexTokenId_;
        layerZeroEndpoint = lzEndpoint_;
        underlyingTokens = tokens_;
        targetAllocations = targetAllocations_;
        lastRebalanceTime = block.timestamp;
        lastFeeCollection = block.timestamp;

        // Set admin as authorized sender
        authorizedSenders[admin] = true;

        emit VaultInitialized(indexTokenId_, asset_, admin);
    }

    /**
     * @dev Deposit with cross-chain message capability
     */
    function deposit(uint256 assets, address receiver) 
        public 
        override(SmartIndexVault, IHyperIndexVault)
        returns (uint256 shares) 
    {
        shares = super.deposit(assets, receiver);

        // Check if auto-rebalancing is needed
        if (_shouldRebalance()) {
            autoRebalance();
        }

        // Emit cross-chain event for tracking
        emit CrossChainMessageSent(receiver, indexTokenId, assets, shares);

        return shares;
    }

    /**
     * @dev Automatic rebalancing based on target allocations
     */
    function autoRebalance() public override {
        require(!paused(), "Vault is paused");

        (bool needed, address fromToken, address toToken, uint256 amount) = checkRebalanceNeeded();

        if (!needed) {
            return;
        }

        uint256 fromIdx = _findTokenIndex(fromToken);
        uint256 toIdx = _findTokenIndex(toToken);

        _executeRebalance(fromIdx, toIdx, amount);

        lastRebalanceTime = block.timestamp;

        emit AutoRebalanced(
            fromToken,
            toToken,
            amount,
            _getCurrentRatio(fromIdx),
            targetAllocations[fromIdx]
        );
    }

    /**
     * @dev Manual rebalancing by authorized users
     */
    function manualRebalance(uint256 fromIdx, uint256 toIdx, uint256 amount) 
        external 
        override 
        onlyAuthorizedSender 
    {
        require(fromIdx < underlyingTokens.length, "Invalid from index");
        require(toIdx < underlyingTokens.length, "Invalid to index");
        require(fromIdx != toIdx, "Same token indices");
        require(amount > 0, "Amount must be positive");

        _executeRebalance(fromIdx, toIdx, amount);

        emit ManualRebalanced(fromIdx, toIdx, amount);
    }

    /**
     * @dev Check if rebalancing is needed
     */
    function checkRebalanceNeeded() 
        public 
        view 
        override 
        returns (bool needed, address fromToken, address toToken, uint256 amount) 
    {
        uint256 totalValue = _getTotalPortfolioValue();
        if (totalValue == 0) {
            return (false, address(0), address(0), 0);
        }

        for (uint256 i = 0; i < underlyingTokens.length; i++) {
            uint256 currentRatio = _getCurrentRatio(i);
            uint256 targetRatio = targetAllocations[i];

            if (currentRatio > targetRatio + rebalanceThreshold) {
                // Need to reduce this token
                for (uint256 j = 0; j < underlyingTokens.length; j++) {
                    if (i == j) continue;

                    uint256 otherCurrentRatio = _getCurrentRatio(j);
                    uint256 otherTargetRatio = targetAllocations[j];

                    if (otherCurrentRatio < otherTargetRatio - rebalanceThreshold) {
                        // Found rebalance opportunity
                        uint256 excessValue = ((currentRatio - targetRatio) * totalValue) / 10000;
                        return (true, underlyingTokens[i], underlyingTokens[j], excessValue);
                    }
                }
            }
        }

        return (false, address(0), address(0), 0);
    }

    /**
     * @dev Get current allocation ratios
     */
    function getAllocationRatios()
        external
        view
        override
        returns (
            address[] memory tokens,
            uint256[] memory currentRatios,
            uint256[] memory targetRatios
        )
    {
        tokens = underlyingTokens;
        targetRatios = targetAllocations;
        currentRatios = new uint256[](underlyingTokens.length);

        for (uint256 i = 0; i < underlyingTokens.length; i++) {
            currentRatios[i] = _getCurrentRatio(i);
        }
    }

    /**
     * @dev Emergency pause function
     */
    function emergencyPause() external override onlyRole(EMERGENCY_ROLE) {
        _pause();
    }

    /**
     * @dev Emergency unpause function
     */
    function emergencyUnpause() external override onlyRole(EMERGENCY_ROLE) {
        _unpause();
    }

    /**
     * @dev Get vault metadata
     */
    function getVaultMetadata()
        external
        view
        override
        returns (
            uint256 _indexTokenId,
            uint256 _totalAssets,
            uint256 _totalShares,
            bool _isPaused,
            uint256 _managementFeeBps,
            uint256 _performanceFeeBps
        )
    {
        return (
            indexTokenId,
            totalAssets(),
            totalSupply(),
            paused(),
            managementFeeBps,
            performanceFeeBps
        );
    }

    /**
     * @dev Get underlying tokens
     */
    function getUnderlyingTokens() external view override returns (address[] memory) {
        return underlyingTokens;
    }

    /**
     * @dev Get LayerZero endpoint
     */
    function getLayerZeroEndpoint() external view override returns (address) {
        return layerZeroEndpoint;
    }

    /**
     * @dev Calculate management fee
     */
    function calculateManagementFee(uint256 assets, uint256 timeElapsed) 
        external 
        view 
        override 
        returns (uint256 fee) 
    {
        // Annual management fee calculation
        fee = (assets * managementFeeBps * timeElapsed) / (10000 * 365 days);
        return fee;
    }

    /**
     * @dev Collect management fees
     */
    function collectManagementFees() external override onlyRole(MANAGER_ROLE) {
        uint256 timeElapsed = block.timestamp - lastFeeCollection;
        uint256 currentAssets = totalAssets();
        uint256 feeAmount = this.calculateManagementFee(currentAssets, timeElapsed);

        if (feeAmount > 0) {
            IERC20(asset()).transfer(msg.sender, feeAmount);
            lastFeeCollection = block.timestamp;
            emit FeeCollected(address(this), feeAmount, block.timestamp);
        }
    }

    /**
     * @dev Set target allocations
     */
    function setTargetAllocations(uint256[] calldata newAllocations) 
        external 
        override 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        require(newAllocations.length == underlyingTokens.length, "Length mismatch");
        require(_validateAllocations(newAllocations), "Invalid allocations");

        targetAllocations = newAllocations;
        emit TargetAllocationsUpdated(newAllocations);
    }

    /**
     * @dev Add new underlying token
     */
    function addUnderlyingToken(address token, uint256 allocation) 
        external 
        override 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        require(token != address(0), "Invalid token");
        require(_findTokenIndex(token) == type(uint256).max, "Token already exists");

        underlyingTokens.push(token);
        targetAllocations.push(allocation);

        require(_validateAllocations(targetAllocations), "Invalid total allocation");

        emit UnderlyingTokenAdded(token, allocation);
    }

    /**
     * @dev Remove underlying token
     */
    function removeUnderlyingToken(uint256 tokenIndex) 
        external 
        override 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        require(tokenIndex < underlyingTokens.length, "Invalid index");

        address removedToken = underlyingTokens[tokenIndex];

        // Remove from arrays
        for (uint256 i = tokenIndex; i < underlyingTokens.length - 1; i++) {
            underlyingTokens[i] = underlyingTokens[i + 1];
            targetAllocations[i] = targetAllocations[i + 1];
        }
        underlyingTokens.pop();
        targetAllocations.pop();

        emit UnderlyingTokenRemoved(tokenIndex);
    }

    // Internal functions

    function _shouldRebalance() internal view returns (bool) {
        return block.timestamp >= lastRebalanceTime + 1 hours; // Rebalance at most once per hour
    }

    function _executeRebalance(uint256 fromIdx, uint256 toIdx, uint256 amount) internal {
        // This would integrate with 1inch or other DEX aggregator
        // For now, this is a placeholder
        emit ManualRebalanced(fromIdx, toIdx, amount);
    }

    function _getCurrentRatio(uint256 tokenIndex) internal view returns (uint256) {
        if (tokenIndex >= underlyingTokens.length) return 0;

        uint256 totalValue = _getTotalPortfolioValue();
        if (totalValue == 0) return 0;

        uint256 tokenValue = _getTokenValue(underlyingTokens[tokenIndex]);
        return (tokenValue * 10000) / totalValue;
    }

    function _getTotalPortfolioValue() internal view returns (uint256) {
        uint256 total = 0;
        for (uint256 i = 0; i < underlyingTokens.length; i++) {
            total += _getTokenValue(underlyingTokens[i]);
        }
        return total;
    }

    function _getTokenValue(address token) internal view returns (uint256) {
        uint256 balance = IERC20(token).balanceOf(address(this));
        // This would use the price feed to get USD value
        // For now, return balance as placeholder
        return balance;
    }

    function _findTokenIndex(address token) internal view returns (uint256) {
        for (uint256 i = 0; i < underlyingTokens.length; i++) {
            if (underlyingTokens[i] == token) {
                return i;
            }
        }
        return type(uint256).max; // Not found
    }

    function _validateAllocations(uint256[] memory allocations) internal pure returns (bool) {
        uint256 total = 0;
        for (uint256 i = 0; i < allocations.length; i++) {
            total += allocations[i];
        }
        return total == 10000; // Must sum to 100%
    }

    // LayerZero message handling (for future use)

    function setTrustedRemote(uint32 chainId, bytes32 trustedRemote) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        trustedRemotes[chainId] = trustedRemote;
        emit TrustedRemoteSet(chainId, trustedRemote);
    }

    function setAuthorizedSender(address sender, bool authorized) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        authorizedSenders[sender] = authorized;
    }

    // Custom event for initialization
    event VaultInitialized(uint256 indexed indexTokenId, address indexed asset, address indexed admin);
}

contracts/interfaces/IHyperIndexVault.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title IHyperIndexVault
 * @dev Interface for HyperIndexVault implementation
 * @notice ERC4626-based vault with cross-chain messaging and rebalancing capabilities
 */
interface IHyperIndexVault {

    // Events
    event CrossChainMessageSent(
        address indexed user, 
        uint256 indexed indexTokenId, 
        uint256 assets, 
        uint256 shares
    );

    event ManualRebalanced(
        uint256 fromIdx, 
        uint256 toIdx, 
        uint256 amount
    );

    event AutoRebalanced(
        address indexed tokenFrom,
        address indexed tokenTo,
        uint256 amount,
        uint256 currentRatio,
        uint256 targetRatio
    );

    event FeeCollected(
        address indexed feeType,
        uint256 amount,
        uint256 timestamp
    );

    /**
     * @dev Initialize the vault with required parameters
     * @param asset_ The underlying ERC20 token
     * @param indexTokenId_ Unique identifier for the index token
     * @param admin Admin address for the vault
     * @param name_ Vault token name
     * @param symbol_ Vault token symbol
     * @param lzEndpoint_ LayerZero endpoint address
     * @param tokens_ Array of underlying tokens to manage
     * @param targetAllocations_ Target allocation percentages (in basis points)
     */
    function initialize(
        address asset_,
        uint256 indexTokenId_,
        address admin,
        string calldata name_,
        string calldata symbol_,
        address lzEndpoint_,
        address[] calldata tokens_,
        uint256[] calldata targetAllocations_
    ) external;

    /**
     * @dev Deposit assets to the vault
     * @param assets Amount of assets to deposit
     * @param receiver Address to receive shares
     * @return shares Amount of shares minted
     */
    function deposit(uint256 assets, address receiver) external returns (uint256 shares);

    /**
     * @dev Automatic rebalancing based on predefined tolerances
     */
    function autoRebalance() external;

    /**
     * @dev Manual rebalancing by admin
     * @param fromIdx Index of token to sell
     * @param toIdx Index of token to buy
     * @param amount Amount to rebalance
     */
    function manualRebalance(uint256 fromIdx, uint256 toIdx, uint256 amount) external;

    /**
     * @dev Check if rebalancing is needed
     * @return needed Whether rebalancing is required
     * @return fromToken Token that needs to be reduced
     * @return toToken Token that needs to be increased
     * @return amount Suggested rebalance amount
     */
    function checkRebalanceNeeded() 
        external 
        view 
        returns (
            bool needed, 
            address fromToken, 
            address toToken, 
            uint256 amount
        );

    /**
     * @dev Get current allocation ratios
     * @return tokens Array of token addresses
     * @return currentRatios Current allocation ratios (in basis points)
     * @return targetRatios Target allocation ratios (in basis points)
     */
    function getAllocationRatios()
        external
        view
        returns (
            address[] memory tokens,
            uint256[] memory currentRatios,
            uint256[] memory targetRatios
        );

    /**
     * @dev Emergency pause function
     */
    function emergencyPause() external;

    /**
     * @dev Emergency unpause function
     */
    function emergencyUnpause() external;

    /**
     * @dev Get vault metadata
     * @return indexTokenId Index token identifier
     * @return totalAssets Total assets under management
     * @return totalShares Total shares outstanding
     * @return isPaused Whether vault is paused
     * @return managementFeeBps Management fee in basis points
     * @return performanceFeeBps Performance fee in basis points
     */
    function getVaultMetadata()
        external
        view
        returns (
            uint256 indexTokenId,
            uint256 totalAssets,
            uint256 totalShares,
            bool isPaused,
            uint256 managementFeeBps,
            uint256 performanceFeeBps
        );

    /**
     * @dev Get underlying tokens
     * @return tokens Array of underlying token addresses
     */
    function getUnderlyingTokens() external view returns (address[] memory tokens);

    /**
     * @dev Get LayerZero endpoint
     * @return endpoint LayerZero endpoint address
     */
    function getLayerZeroEndpoint() external view returns (address endpoint);

    /**
     * @dev Calculate management fee
     * @param assets Assets amount
     * @param timeElapsed Time elapsed since last fee collection
     * @return fee Management fee amount
     */
    function calculateManagementFee(uint256 assets, uint256 timeElapsed) 
        external 
        view 
        returns (uint256 fee);

    /**
     * @dev Collect management fees
     */
    function collectManagementFees() external;

    /**
     * @dev Set target allocations (admin only)
     * @param newAllocations New target allocations (must sum to 10000)
     */
    function setTargetAllocations(uint256[] calldata newAllocations) external;

    /**
     * @dev Add new underlying token (admin only)
     * @param token Token address to add
     * @param allocation Target allocation for new token
     */
    function addUnderlyingToken(address token, uint256 allocation) external;

    /**
     * @dev Remove underlying token (admin only)
     * @param tokenIndex Index of token to remove
     */
    function removeUnderlyingToken(uint256 tokenIndex) external;
}

contracts/mocks/MockLayerZeroEndpoint.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title MockLayerZeroEndpoint
 * @dev Mock implementation of LayerZero endpoint for testing purposes
 * @notice This simulates LayerZero V2 endpoint functionality for HyperEVM testnet
 */
contract MockLayerZeroEndpoint {

    uint32 public eid; // Endpoint ID
    address public owner;

    // Message tracking
    uint64 private nextNonce = 1;
    mapping(address => mapping(uint32 => uint64)) public outboundNonce;
    mapping(address => mapping(uint32 => uint64)) public inboundNonce;

    // Message queue for simulation
    struct PendingMessage {
        uint32 srcEid;
        bytes32 sender;
        address receiver;
        bytes message;
        uint256 timestamp;
        bool executed;
    }

    PendingMessage[] public pendingMessages;
    mapping(bytes32 => bool) public messageExecuted;

    // Events
    event PacketSent(
        uint32 indexed dstEid,
        address indexed sender,
        address indexed receiver,
        bytes message,
        uint64 nonce
    );

    event PacketReceived(
        uint32 indexed srcEid,
        bytes32 indexed sender,
        address indexed receiver,
        bytes message,
        uint64 nonce
    );

    event MessageExecuted(
        bytes32 indexed messageHash,
        bool success
    );

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor(uint32 _eid) {
        eid = _eid;
        owner = msg.sender;
    }

    /**
     * @dev Send a message to another chain (simulated)
     * @param _dstEid Destination endpoint ID
     * @param _receiver Receiver address on destination chain
     * @param _message Message payload
     * @param _options Execution options (ignored in mock)
     * @param _fee Fee for sending (ignored in mock)
     */
    function send(
        uint32 _dstEid,
        address _receiver,
        bytes calldata _message,
        bytes calldata _options,
        uint256 _fee
    ) external payable returns (uint64 nonce) {
        nonce = nextNonce++;
        outboundNonce[msg.sender][_dstEid] = nonce;

        emit PacketSent(_dstEid, msg.sender, _receiver, _message, nonce);

        // In a real implementation, this would be handled by LayerZero network
        // For testing, we simulate immediate delivery
        _simulateMessageDelivery(_dstEid, msg.sender, _receiver, _message, nonce);

        return nonce;
    }

    /**
     * @dev Simulate message delivery (for testing only)
     */
    function _simulateMessageDelivery(
        uint32 _srcEid,
        address _sender,
        address _receiver,
        bytes memory _message,
        uint64 _nonce
    ) internal {
        // Create message hash
        bytes32 messageHash = keccak256(abi.encodePacked(_srcEid, _sender, _receiver, _message, _nonce));

        // Add to pending messages
        pendingMessages.push(PendingMessage({
            srcEid: _srcEid,
            sender: bytes32(uint256(uint160(_sender))),
            receiver: _receiver,
            message: _message,
            timestamp: block.timestamp,
            executed: false
        }));

        emit PacketReceived(_srcEid, bytes32(uint256(uint160(_sender))), _receiver, _message, _nonce);
    }

    /**
     * @dev Execute a pending message (simulating LayerZero execution)
     * @param _messageIndex Index of message in pending messages array
     */
    function executeMessage(uint256 _messageIndex) external {
        require(_messageIndex < pendingMessages.length, "Invalid message index");

        PendingMessage storage message = pendingMessages[_messageIndex];
        require(!message.executed, "Message already executed");

        bytes32 messageHash = keccak256(abi.encodePacked(
            message.srcEid,
            message.sender,
            message.receiver,
            message.message,
            _messageIndex
        ));

        require(!messageExecuted[messageHash], "Message hash already executed");

        message.executed = true;
        messageExecuted[messageHash] = true;
        inboundNonce[message.receiver][message.srcEid]++;

        // Try to execute the message on the receiver contract
        bool success = false;
        if (message.receiver.code.length > 0) {
            try ILayerZeroReceiver(message.receiver).lzReceive(
                message.srcEid,
                message.sender,
                inboundNonce[message.receiver][message.srcEid],
                message.message
            ) {
                success = true;
            } catch {
                success = false;
            }
        }

        emit MessageExecuted(messageHash, success);
    }

    /**
     * @dev Get quote for sending a message (always returns 0 for mock)
     */
    function quote(
        uint32 _dstEid,
        address _sender,
        bytes calldata _message,
        bytes calldata _options
    ) external pure returns (uint256 fee) {
        // Mock implementation - no fees
        return 0;
    }

    /**
     * @dev Get pending messages count
     */
    function getPendingMessagesCount() external view returns (uint256) {
        return pendingMessages.length;
    }

    /**
     * @dev Get pending message details
     */
    function getPendingMessage(uint256 _index) external view returns (
        uint32 srcEid,
        bytes32 sender,
        address receiver,
        bytes memory msgData,
        uint256 timestamp,
        bool executed
    ) {
        require(_index < pendingMessages.length, "Invalid index");
        PendingMessage memory pendingMsg = pendingMessages[_index];
        return (pendingMsg.srcEid, pendingMsg.sender, pendingMsg.receiver, pendingMsg.message, pendingMsg.timestamp, pendingMsg.executed);
    }

    /**
     * @dev Set endpoint ID (only for testing)
     */
    function setEid(uint32 _newEid) external onlyOwner {
        eid = _newEid;
    }
}

/**
 * @title ILayerZeroReceiver
 * @dev Interface for contracts that can receive LayerZero messages
 */
interface ILayerZeroReceiver {
    function lzReceive(
        uint32 _srcEid,
        bytes32 _sender,
        uint64 _nonce,
        bytes calldata _message
    ) external;
}

contracts/SmartIndexVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title SmartIndexVault
 * @dev ERC-4626 compliant tokenized vault for index fund management
 * @notice Implements full ERC-4626 standard with additional security features
 */
contract SmartIndexVault is ERC4626, AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // Role definitions
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    bytes32 public constant STRATEGIST_ROLE = keccak256("STRATEGIST_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");

    // State variables
    uint256 public managementFee = 200; // 2% annual (basis points)
    uint256 public performanceFee = 2000; // 20% of profits (basis points)
    uint256 public lastHarvestTime;
    uint256 public lastTotalAssets;
    uint256 public highWaterMark;

    // Constants
    uint256 public constant FEE_DENOMINATOR = 10000;
    uint256 public constant SECONDS_PER_YEAR = 365 days;
    uint256 public constant MAX_MANAGEMENT_FEE = 500; // 5% max
    uint256 public constant MAX_PERFORMANCE_FEE = 3000; // 30% max

    // Emergency withdrawal
    address public emergencyWithdrawalRecipient;

    // Events
    event Harvest(uint256 profit, uint256 performanceFeeAmount, uint256 timestamp);
    event ManagementFeeUpdated(uint256 oldFee, uint256 newFee);
    event PerformanceFeeUpdated(uint256 oldFee, uint256 newFee);
    event EmergencyWithdrawal(address token, uint256 amount, address recipient);
    event StrategyExecuted(string strategyName, uint256 timestamp);

    /**
     * @dev Constructor - used as template for cloning
     * @param _asset The underlying asset token (can be zero for template)
     * @param _name Vault share token name
     * @param _symbol Vault share token symbol
     */
    constructor(
        IERC20 _asset,
        string memory _name,
        string memory _symbol
    ) ERC4626(_asset) ERC20(_name, _symbol) {
        // Template initialization - actual initialization happens in initialize()
        if (address(_asset) != address(0)) {
            _initializeVault(msg.sender, msg.sender, msg.sender);
        }
    }

    /**
     * @dev Initialize function for cloned instances
     * @param _asset The underlying asset token
     * @param _name Vault share token name
     * @param _symbol Vault share token symbol
     * @param _managementFee Management fee in basis points
     * @param _performanceFee Performance fee in basis points
     */
    function initialize(
        IERC20 _asset,
        string memory _name,
        string memory _symbol,
        uint256 _managementFee,
        uint256 _performanceFee
    ) external {
        require(address(asset()) == address(0), "Already initialized");
        require(address(_asset) != address(0), "Invalid asset");
        require(_managementFee <= MAX_MANAGEMENT_FEE, "Management fee too high");
        require(_performanceFee <= MAX_PERFORMANCE_FEE, "Performance fee too high");

        // Initialize ERC4626 and ERC20
        _setAsset(_asset);
        _setName(_name);
        _setSymbol(_symbol);

        // Set fees
        managementFee = _managementFee;
        performanceFee = _performanceFee;

        // Initialize vault state
        _initializeVault(msg.sender, msg.sender, msg.sender);
    }

    /**
     * @dev Internal function to initialize vault roles and state
     */
    function _initializeVault(address admin, address manager, address emergency) internal {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(MANAGER_ROLE, manager);
        _grantRole(EMERGENCY_ROLE, emergency);

        lastHarvestTime = block.timestamp;
        lastTotalAssets = 0;
        highWaterMark = 0;
        emergencyWithdrawalRecipient = admin;
    }

    /**
     * @dev Set asset for initialized vault (internal use only)
     */
    function _setAsset(IERC20 newAsset) internal {
        // This would need to be implemented in a way that's compatible with ERC4626
        // For now, we'll assume the asset is set during construction/initialization
    }

    /**
     * @dev Set name for initialized vault (internal use only)
     */
    function _setName(string memory newName) internal {
        // This would update the ERC20 name
        // Implementation depends on whether the name is mutable
    }

    /**
     * @dev Set symbol for initialized vault (internal use only)
     */
    function _setSymbol(string memory newSymbol) internal {
        // This would update the ERC20 symbol
        // Implementation depends on whether the symbol is mutable
    }

    /**
     * @dev Returns the total assets under management
     * @notice Overrides ERC4626 to include yield strategies
     */
    function totalAssets() public view override returns (uint256) {
        uint256 baseAssets = super.totalAssets();
        uint256 accruedFees = _calculateAccruedManagementFee();

        // Deduct accrued fees from total assets
        return baseAssets > accruedFees ? baseAssets - accruedFees : 0;
    }

    /**
     * @dev Deposit assets and receive shares
     * @notice Implements pausable modifier for safety
     */
    function deposit(uint256 assets, address receiver) 
        public 
        virtual
        override 
        nonReentrant 
        whenNotPaused 
        returns (uint256) 
    {
        return super.deposit(assets, receiver);
    }

    /**
     * @dev Mint shares by depositing assets
     * @notice Implements pausable modifier for safety
     */
    function mint(uint256 shares, address receiver) 
        public 
        override 
        nonReentrant 
        whenNotPaused 
        returns (uint256) 
    {
        return super.mint(shares, receiver);
    }

    /**
     * @dev Withdraw assets by burning shares
     * @notice Implements reentrancy protection
     */
    function withdraw(
        uint256 assets,
        address receiver,
        address owner
    ) public override nonReentrant returns (uint256) {
        return super.withdraw(assets, receiver, owner);
    }

    /**
     * @dev Redeem shares for assets
     * @notice Implements reentrancy protection
     */
    function redeem(
        uint256 shares,
        address receiver,
        address owner
    ) public override nonReentrant returns (uint256) {
        return super.redeem(shares, receiver, owner);
    }

    /**
     * @dev Preview deposit to get expected shares
     * @notice Accounts for management fees
     */
    function previewDeposit(uint256 assets) public view override returns (uint256) {
        return super.previewDeposit(assets);
    }

    /**
     * @dev Preview mint to get required assets
     * @notice Accounts for management fees
     */
    function previewMint(uint256 shares) public view override returns (uint256) {
        return super.previewMint(shares);
    }

    /**
     * @dev Preview withdraw to get shares to burn
     * @notice Accounts for management fees
     */
    function previewWithdraw(uint256 assets) public view override returns (uint256) {
        return super.previewWithdraw(assets);
    }

    /**
     * @dev Preview redeem to get expected assets
     * @notice Accounts for management fees
     */
    function previewRedeem(uint256 shares) public view override returns (uint256) {
        return super.previewRedeem(shares);
    }

    /**
     * @dev Harvest yields and collect fees
     * @notice Can only be called by managers
     */
    function harvest() external onlyRole(MANAGER_ROLE) {
        uint256 currentAssets = totalAssets();
        uint256 profit = 0;

        // Calculate profit if above high water mark
        if (currentAssets > highWaterMark) {
            profit = currentAssets - highWaterMark;
            highWaterMark = currentAssets;
        }

        // Calculate and collect performance fee
        uint256 performanceFeeAmount = 0;
        if (profit > 0) {
            performanceFeeAmount = (profit * performanceFee) / FEE_DENOMINATOR;
            // Transfer performance fee to treasury
            IERC20(asset()).safeTransfer(msg.sender, performanceFeeAmount);
        }

        // Calculate and collect management fee
        uint256 managementFeeAmount = _calculateAccruedManagementFee();
        if (managementFeeAmount > 0) {
            IERC20(asset()).safeTransfer(msg.sender, managementFeeAmount);
        }

        lastHarvestTime = block.timestamp;
        lastTotalAssets = currentAssets - performanceFeeAmount - managementFeeAmount;

        emit Harvest(profit, performanceFeeAmount, block.timestamp);
    }

    /**
     * @dev Calculate accrued management fee
     */
    function _calculateAccruedManagementFee() private view returns (uint256) {
        uint256 timePassed = block.timestamp - lastHarvestTime;
        uint256 currentAssets = super.totalAssets();

        // Annual fee calculation prorated by time
        uint256 feeAmount = (currentAssets * managementFee * timePassed) / 
                           (FEE_DENOMINATOR * SECONDS_PER_YEAR);

        return feeAmount;
    }

    /**
     * @dev Update management fee
     * @param newFee New management fee in basis points
     */
    function setManagementFee(uint256 newFee) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newFee <= MAX_MANAGEMENT_FEE, "Fee too high");
        uint256 oldFee = managementFee;
        managementFee = newFee;
        emit ManagementFeeUpdated(oldFee, newFee);
    }

    /**
     * @dev Update performance fee
     * @param newFee New performance fee in basis points
     */
    function setPerformanceFee(uint256 newFee) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newFee <= MAX_PERFORMANCE_FEE, "Fee too high");
        uint256 oldFee = performanceFee;
        performanceFee = newFee;
        emit PerformanceFeeUpdated(oldFee, newFee);
    }

    /**
     * @dev Execute yield strategy
     * @param strategyName Name of the strategy for tracking
     */
    function executeStrategy(string memory strategyName) 
        external 
        onlyRole(STRATEGIST_ROLE) 
        whenNotPaused 
    {
        // Strategy implementation would go here
        // This is a placeholder for actual yield farming logic

        emit StrategyExecuted(strategyName, block.timestamp);
    }

    /**
     * @dev Emergency pause
     */
    function pause() external onlyRole(EMERGENCY_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause operations
     */
    function unpause() external onlyRole(EMERGENCY_ROLE) {
        _unpause();
    }

    /**
     * @dev Emergency withdrawal of stuck tokens
     * @param token Address of the token to withdraw
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) 
        external 
        onlyRole(EMERGENCY_ROLE) 
    {
        require(token != asset(), "Cannot withdraw vault asset");
        IERC20(token).safeTransfer(emergencyWithdrawalRecipient, amount);
        emit EmergencyWithdrawal(token, amount, emergencyWithdrawalRecipient);
    }

    /**
     * @dev Set emergency withdrawal recipient
     * @param recipient New recipient address
     */
    function setEmergencyRecipient(address recipient) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        require(recipient != address(0), "Invalid recipient");
        emergencyWithdrawalRecipient = recipient;
    }


    /**
     * @dev Returns the maximum amount of assets that can be deposited
     */
    function maxDeposit(address) public view override returns (uint256) {
        return paused() ? 0 : type(uint256).max;
    }

    /**
     * @dev Returns the maximum amount of shares that can be minted
     */
    function maxMint(address) public view override returns (uint256) {
        return paused() ? 0 : type(uint256).max;
    }

    /**
     * @dev Returns the maximum amount of assets that can be withdrawn
     */
    function maxWithdraw(address owner) public view override returns (uint256) {
        return paused() ? 0 : super.maxWithdraw(owner);
    }

    /**
     * @dev Returns the maximum amount of shares that can be redeemed
     */
    function maxRedeem(address owner) public view override returns (uint256) {
        return paused() ? 0 : super.maxRedeem(owner);
    }
}

